import { z } from "zod";

const CATLIFE_API_URL =
  "https://sydle-ui-dev.sydle.one/api/1/main/exemplosJose/ExampleAPI/getCatlifeBlogContent";

const CatlifePostSchema = z.object({
  tag: z.string(),
  titulo: z.string(),
  data: z.string(),
});

export type CatlifePost = z.infer<typeof CatlifePostSchema>;

export async function getCatlifePosts(): Promise<CatlifePost[]> {
  try {
    const response = await fetch(CATLIFE_API_URL, {
      // Cache on the server for 10 minutes; allow SWR
      next: { revalidate: 600 },
      headers: {
        accept: "application/json",
      },
    });

    if (!response.ok) {
      throw new Error("Failed to fetch catlife posts");
    }

    const data = await response.json();

    return z.array(CatlifePostSchema).parse(data);
  } catch (error) {
    if (error instanceof z.ZodError) {
      console.error("Validation error:", error.issues);
    } else {
      console.error("Error fetching catlife posts:", error);
    }
    throw error;
  }
}

import { NextResponse } from "next/server";

import { getCatlifePosts } from "@/lib/catlife-posts";

export async function GET() {
  try {
    const posts = await getCatlifePosts();

    return NextResponse.json(posts, {
      headers: {
        "Cache-Control": "s-maxage=600, stale-while-revalidate=300",
      },
    });
  } catch (error) {
    console.error("Error in API route:", error);
    return NextResponse.json(
      { error: "Failed to fetch catlife posts" },
      { status: 500 },
    );
  }
}

"use client";

import { useEffect, useState } from "react";

import type { CatlifePost } from "@/lib/catlife-posts";

interface UseCatlifePostsResult {
  posts: CatlifePost[];
  isLoading: boolean;
  error: Error | null;
}

export function useCatlifePosts(
  apiUrl: string = "/api/catlife-posts",
): UseCatlifePostsResult {
  const [posts, setPosts] = useState<CatlifePost[]>([]);
  const [isLoading, setIsLoading] = useState<boolean>(true);
  const [error, setError] = useState<Error | null>(null);

  useEffect(() => {
    let isMounted = true;

    async function fetchPosts() {
      try {
        setIsLoading(true);
        setError(null);

        const response = await window.fetch(apiUrl);

        if (!response.ok) {
          throw new Error(`Failed to fetch posts: ${response.statusText}`);
        }

        const data = await response.json();

        if (isMounted) {
          setPosts(data as CatlifePost[]);
        }
      } catch (err) {
        if (isMounted) {
          setError(err instanceof Error ? err : new Error("Unknown error"));
        }
      } finally {
        if (isMounted) {
          setIsLoading(false);
        }
      }
    }

    fetchPosts();

    return () => {
      isMounted = false;
    };
  }, [apiUrl]);

  return { posts, isLoading, error };
}
