import * as React from "react"
import { cn } from "@/lib/utils"

export interface PillContainerProps extends React.HTMLAttributes<HTMLDivElement> {
  children: React.ReactNode
}

const PillContainer = React.forwardRef<HTMLDivElement, PillContainerProps>(({ className, children, ...props }, ref) => {
  return (
    <div
      ref={ref}
      className={cn(
        "flex items-center justify-between",
        "bg-catlife-background/80",
        "backdrop-blur-xl backdrop-saturate-150",
        "border-2 border-white/60",
        "rounded-full",
        "px-6 py-3",
        "w-full",
        "gap-4",
        className,
      )}
      {...props}
    >
      {children}
    </div>
  )
})

PillContainer.displayName = "PillContainer"

export { PillContainer }

import { Geist, Geist_Mono, Montserrat } from "next/font/google";
import React, { type ReactNode } from "react";

import { cn } from "@/lib/utils";

import "@/app/globals.css";

const GeistSans = Geist({
  subsets: ["latin"],
  variable: "--font-sans",
});

const GeistMono = Geist_Mono({
  subsets: ["latin"],
  variable: "--font-mono",
});

const MontserratSerif = Montserrat({
  subsets: ["latin"],
  variable: "--font-serif",
});

export default function RootLayout({
  children,
}: Readonly<{
  children: ReactNode;
}>) {
  return (
    <html
      lang="en"
      className={cn(
        GeistSans.variable,
        GeistMono.variable,
        MontserratSerif.variable,
        "bg-background text-foreground",
      )}
    >
      <body>
        <main className="mt-16 flex w-full justify-center">
          <div className="container">{children}</div>
        </main>
      </body>
    </html>
  );
}
