@import "tailwindcss";

@import "tw-animate-css";

@custom-variant dark (&:is(.dark *));

:root {
  /* Standard shadcn tokens - converted to OKLCH, matching Figma values */
  --background: oklch(0.985 0.01 80); /* #fffaf3 */
  --foreground: oklch(0.15 0 0); /* #161616 */
  --card: oklch(0.985 0.01 80); /* #fffaf3 */
  --card-foreground: oklch(0.15 0 0); /* #161616 */
  --popover: oklch(0.985 0.01 80); /* #fffaf3 */
  --popover-foreground: oklch(0.15 0 0); /* #161616 */
  --primary: oklch(0.65 0.15 70); /* #f99830 */
  --primary-foreground: oklch(0.15 0 0); /* #161616 */
  --secondary: oklch(0.88 0.08 75); /* #ffd094 */
  --secondary-foreground: oklch(0.15 0 0); /* #161616 */
  --muted: oklch(0.96 0.02 80); /* #ffeed6 */
  --muted-foreground: oklch(0.15 0 0); /* #161616 */
  --accent: oklch(0.88 0.08 75); /* #ffd094 */
  --accent-foreground: oklch(0.15 0 0); /* #161616 */
  --destructive: oklch(0.60 0.22 25); /* #ef4444 */
  --destructive-foreground: oklch(1 0 0); /* #ffffff */
  --border: oklch(0.15 0 0); /* #161616 */
  --input: oklch(0.15 0 0); /* #161616 */
  --ring: oklch(0.65 0.15 70); /* #f99830 */
  --chart-1: oklch(0.65 0.15 70); /* #f99830 */
  --chart-2: oklch(0.65 0.15 70); /* #f99830 */
  --chart-3: oklch(0.58 0.18 60); /* #f27100 */
  --chart-4: oklch(0.65 0.20 165); /* #10b981 */
  --chart-5: oklch(0.88 0.08 75); /* #ffd094 */
  --radius: 1.5rem;
  --sidebar: oklch(0.985 0.01 80); /* #fffaf3 */
  --sidebar-foreground: oklch(0.15 0 0); /* #161616 */
  --sidebar-primary: oklch(0.65 0.15 70); /* #f99830 */
  --sidebar-primary-foreground: oklch(0.15 0 0); /* #161616 */
  --sidebar-accent: oklch(0.96 0.02 80); /* #ffeed6 */
  --sidebar-accent-foreground: oklch(0.15 0 0); /* #161616 */
  --sidebar-border: oklch(0.15 0 0); /* #161616 */
  --sidebar-ring: oklch(0.65 0.15 70); /* #f99830 */

  /* Custom Catlife tokens - using hex colors directly from Figma */
  --catlife-background: #fffaf3;
  --catlife-background-alt: #ffeed6;
  --catlife-surface: #fffaf3;
  --catlife-border: #161616;
  --catlife-primary: #f99830;
  --catlife-primary-hover: #ffa63a;
  --catlife-primary-active: #f27100;
  --catlife-primary-light: #ffd094;
  --catlife-primary-lightest: #ffeed6;
  --catlife-primary-dark: #f27100;
  --catlife-primary-darker: #fe6b1c;
  --catlife-accent-success: #10b981;
  --catlife-text-primary: #161616;
  --catlife-text-secondary: #78716c;
  --catlife-text-muted: #a8a29e;
  --catlife-text-inverse: #fffaf3;
  --catlife-text-variant-1: #44403c;
  --catlife-text-variant-2: #1E4146;
}

.dark {
  /* Standard shadcn tokens - dark mode */
  --background: oklch(0.145 0 0);
  --foreground: oklch(0.985 0 0);
  --card: oklch(0.145 0 0);
  --card-foreground: oklch(0.985 0 0);
  --popover: oklch(0.145 0 0);
  --popover-foreground: oklch(0.985 0 0);
  --primary: oklch(0.985 0 0);
  --primary-foreground: oklch(0.205 0 0);
  --secondary: oklch(0.269 0 0);
  --secondary-foreground: oklch(0.985 0 0);
  --muted: oklch(0.269 0 0);
  --muted-foreground: oklch(0.708 0 0);
  --accent: oklch(0.269 0 0);
  --accent-foreground: oklch(0.985 0 0);
  --destructive: oklch(0.396 0.141 25.723);
  --destructive-foreground: oklch(0.637 0.237 25.331);
  --border: oklch(0.269 0 0);
  --input: oklch(0.269 0 0);
  --ring: oklch(0.439 0 0);
  --chart-1: oklch(0.488 0.243 264.376);
  --chart-2: oklch(0.696 0.17 162.48);
  --chart-3: oklch(0.769 0.188 70.08);
  --chart-4: oklch(0.627 0.265 303.9);
  --chart-5: oklch(0.645 0.246 16.439);
  --sidebar: oklch(0.205 0 0);
  --sidebar-foreground: oklch(0.985 0 0);
  --sidebar-primary: oklch(0.488 0.243 264.376);
  --sidebar-primary-foreground: oklch(0.985 0 0);
  --sidebar-accent: oklch(0.269 0 0);
  --sidebar-accent-foreground: oklch(0.985 0 0);
  --sidebar-border: oklch(0.269 0 0);
  --sidebar-ring: oklch(0.439 0 0);

  /* Custom Catlife tokens - dark mode */
  --catlife-background: oklch(0.145 0 0);
  --catlife-background-alt: oklch(0.205 0 0);
  --catlife-surface: oklch(0.145 0 0);
  --catlife-border: oklch(0.269 0 0);
  --catlife-primary: oklch(0.65 0.15 70);
  --catlife-primary-hover: oklch(0.72 0.12 70);
  --catlife-primary-active: oklch(0.58 0.18 60);
  --catlife-primary-light: oklch(0.50 0.10 70);
  --catlife-primary-lightest: oklch(0.40 0.08 70);
  --catlife-primary-dark: oklch(0.58 0.18 60);
  --catlife-primary-darker: oklch(0.55 0.20 50);
  --catlife-accent-success: oklch(0.65 0.20 165);
  --catlife-text-primary: oklch(0.985 0 0);
  --catlife-text-secondary: oklch(0.708 0 0);
  --catlife-text-muted: oklch(0.60 0 0);
  --catlife-text-inverse: oklch(0.145 0 0);
  --catlife-text-variant-1: oklch(0.80 0 0);
  --catlife-text-variant-2: oklch(0.70 0.02 200);
}

@theme inline {
  /* Standard shadcn tokens mapped for Tailwind */
  --color-background: var(--background);
  --color-foreground: var(--foreground);
  --color-card: var(--card);
  --color-card-foreground: var(--card-foreground);
  --color-popover: var(--popover);
  --color-popover-foreground: var(--popover-foreground);
  --color-primary: var(--primary);
  --color-primary-foreground: var(--primary-foreground);
  --color-secondary: var(--secondary);
  --color-secondary-foreground: var(--secondary-foreground);
  --color-muted: var(--muted);
  --color-muted-foreground: var(--muted-foreground);
  --color-accent: var(--accent);
  --color-accent-foreground: var(--accent-foreground);
  --color-destructive: var(--destructive);
  --color-destructive-foreground: var(--destructive-foreground);
  --color-border: var(--border);
  --color-input: var(--input);
  --color-ring: var(--ring);
  --color-chart-1: var(--chart-1);
  --color-chart-2: var(--chart-2);
  --color-chart-3: var(--chart-3);
  --color-chart-4: var(--chart-4);
  --color-chart-5: var(--chart-5);
  --radius-sm: calc(var(--radius) - 4px);
  --radius-md: calc(var(--radius) - 2px);
  --radius-lg: var(--radius);
  --radius-xl: calc(var(--radius) + 4px);
  --color-sidebar: var(--sidebar);
  --color-sidebar-foreground: var(--sidebar-foreground);
  --color-sidebar-primary: var(--sidebar-primary);
  --color-sidebar-primary-foreground: var(--sidebar-primary-foreground);
  --color-sidebar-accent: var(--sidebar-accent);
  --color-sidebar-accent-foreground: var(--sidebar-accent-foreground);
  --color-sidebar-border: var(--sidebar-border);
  --color-sidebar-ring: var(--sidebar-ring);

  /* Custom Catlife tokens mapped for Tailwind */
  --color-catlife-background: var(--catlife-background);
  --color-catlife-background-alt: var(--catlife-background-alt);
  --color-catlife-surface: var(--catlife-surface);
  --color-catlife-border: var(--catlife-border);
  --color-catlife-primary: var(--catlife-primary);
  --color-catlife-primary-hover: var(--catlife-primary-hover);
  --color-catlife-primary-active: var(--catlife-primary-active);
  --color-catlife-primary-light: var(--catlife-primary-light);
  --color-catlife-primary-lightest: var(--catlife-primary-lightest);
  --color-catlife-primary-dark: var(--catlife-primary-dark);
  --color-catlife-primary-darker: var(--catlife-primary-darker);
  --color-catlife-accent-success: var(--catlife-accent-success);
  --color-catlife-text-primary: var(--catlife-text-primary);
  --color-catlife-text-secondary: var(--catlife-text-secondary);
  --color-catlife-text-muted: var(--catlife-text-muted);
  --color-catlife-text-inverse: var(--catlife-text-inverse);
  --color-catlife-text-variant-1: var(--catlife-text-variant-1);
  --color-catlife-text-variant-2: var(--catlife-text-variant-2);
}

@layer base {
  * {
    @apply border-border outline-ring/50;
  }

  body {
    @apply bg-background text-foreground;
    font-family: var(--font-sans), sans-serif;
  }

  /* Font family utilities */
  .font-roc-grotesk {
    font-family: var(--font-sans), "Roc Grotesk", sans-serif;
  }

  .font-lato {
    font-family: var(--font-lato), "Lato", sans-serif;
  }
}

/* ============================================
   Catlife Component Layer
   ============================================ */

@layer components {
  /* Catlife Button - Primary CTA with shadow effects */
  .catlife-btn {
    @apply inline-flex items-center justify-center gap-2 rounded-full border-none bg-primary px-6 py-3 font-semibold text-base text-primary-foreground transition-all duration-300;
    box-shadow: 0 2px 8px rgba(244, 152, 52, 0.3);
  }

  .catlife-btn:hover {
    @apply -translate-y-px;
    background-color: color-mix(in srgb, var(--color-primary) 90%, transparent);
    box-shadow: 0 4px 12px rgba(244, 152, 52, 0.4);
  }

  .catlife-btn:active {
    @apply translate-y-0;
    box-shadow: 0 1px 4px rgba(244, 152, 52, 0.3);
  }

  /* Catlife Button Sizes */
  .catlife-btn-sm {
    @apply px-4 py-2 text-sm;
  }

  .catlife-btn-lg {
    @apply px-8 py-4 text-lg;
  }

  /* Catlife Card - Article/Content cards */
  .catlife-card {
    @apply cursor-pointer overflow-hidden rounded-2xl bg-card transition-transform duration-300 hover:scale-[1.02];
  }

  .catlife-card-image {
    @apply relative overflow-hidden;
    aspect-ratio: 4 / 3;
  }

  .catlife-card-image img {
    @apply object-cover transition-transform duration-300 group-hover:scale-105;
  }

  .catlife-card-content {
    @apply flex flex-col gap-3 p-4;
  }

  /* Catlife Featured Card - Hero article */
  .catlife-featured-card {
    @apply grid grid-cols-1 items-center gap-8 md:grid-cols-2;
  }

  .catlife-featured-image {
    @apply relative overflow-hidden rounded-3xl;
    aspect-ratio: 4 / 3;
  }

  /* Catlife Category Tag */
  .catlife-category {
    @apply inline-block text-sm font-medium uppercase tracking-wider text-primary;
  }

  /* Catlife Section - Large rounded sections */
  .catlife-section {
    @apply rounded-3xl p-12;
  }

  .catlife-section-newsletter {
    @apply mx-auto max-w-lg text-center;
    background-color: color-mix(in srgb, var(--color-accent) 30%, transparent);
  }

  /* Catlife Header */
  .catlife-header {
    @apply border-b;
    border-bottom-color: color-mix(in srgb, var(--color-foreground) 10%, transparent);
  }

  .catlife-nav {
    @apply flex items-center gap-6;
  }

  .catlife-nav-link {
    @apply cursor-pointer border-none bg-none text-base text-foreground transition-colors duration-200 hover:text-primary;
  }

  /* Catlife Footer */
  .catlife-footer {
    @apply mt-16 py-6;
    background-color: color-mix(in srgb, var(--color-accent) 50%, transparent);
  }

  /* Pill Container - Glassmorphism style */
  .catlife-pill {
    @apply rounded-full px-6 py-3 shadow-sm backdrop-blur-md;
    border: 1px solid color-mix(in srgb, var(--color-foreground) 10%, transparent);
    background-color: rgba(255, 255, 255, 0.8);
  }
}

/* ============================================
   Catlife Utility Layer
   ============================================ */

@layer utilities {
  .text-balance {
    text-wrap: balance;
  }

  .text-pretty {
    text-wrap: pretty;
  }

  /* Catlife Shadow Utilities */
  .shadow-catlife-default {
    box-shadow: 2px 2px 0px 0px rgba(22, 22, 22, 1);
  }

  .shadow-catlife-hover {
    box-shadow: 3px 3px 0px 0px rgba(22, 22, 22, 1);
  }

  .shadow-catlife-active {
    box-shadow: 1px 1px 0px 0px rgba(22, 22, 22, 1);
  }

  /* Catlife CTA Shadow Utilities - Enhanced emphasis */
  .shadow-catlife-cta-default {
    box-shadow: 3px 3px 0px 0px rgba(22, 22, 22, 1);
  }

  .shadow-catlife-cta-hover {
    box-shadow: 4px 4px 0px 0px rgba(22, 22, 22, 1);
  }

  .shadow-catlife-cta-active {
    box-shadow: 2px 2px 0px 0px rgba(22, 22, 22, 1);
  }

  /* Catlife Dark Shadow Utilities - Orange color scheme */
  .shadow-catlife-dark-default {
    box-shadow: 2px 2px 0px 0px rgba(249, 152, 48, 1);
  }

  .shadow-catlife-dark-hover {
    box-shadow: 3px 3px 0px 0px rgba(249, 152, 48, 1);
  }

  .shadow-catlife-dark-active {
    box-shadow: 1px 1px 0px 0px rgba(242, 113, 0, 1);
  }
}
